import React, { useEffect, useMemo, useRef, useState } from "react";
import PomodoroTimer from "./PomodoroTimer";
import LiveBackground from "./LiveBackground";

// ===== Types (inline to avoid extra files)
type Filter = "all" | "active" | "completed" | "matrix";
type Priority = "UI" | "UN" | "NI" | "NN"; // Eisenhower
interface Task {
  id: number;
  text: string;
  completed: boolean;
  createdAt: number;
  dueAt?: string;
  remind?: boolean;
  notified?: boolean;
  pomos?: number;
  // Phase 2
  priority?: Priority;
  goalId?: number | null;
}
interface Goal {
  id: number;
  title: string;
  emoji?: string;
}

// ===== Storage keys
const TASKS_KEY = "task-tracker.tasks.v4";
const GOALS_KEY = "task-tracker.goals.v1";
const GOAL_PREF_KEY = "task-tracker.goalPref";
const WELLNESS_PREFIX = "wellness.entry."; // wellness.entry.YYYY-MM-DD

function todayKey() {
  const d = new Date();
  const yyyy = d.getFullYear();
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const dd = String(d.getDate()).padStart(2, "0");
  return `${WELLNESS_PREFIX}${yyyy}-${mm}-${dd}`;
}

const App: React.FC = () => {
  // ===== Core state
  const [tasks, setTasks] = useState<Task[]>([]);
  const [input, setInput] = useState("");
  const [dueAt, setDueAt] = useState<string>("");
  const [remind, setRemind] = useState<boolean>(false);
  const [filter, setFilter] = useState<Filter>("all");
  const [activeTaskId, setActiveTaskId] = useState<number | null>(null);

  // ===== Phase 1: Wellness
  const [mood, setMood] = useState<number>(3);    // 1..5
  const [stress, setStress] = useState<number>(3); // 0..10

  // ===== Phase 2: Goals + Priority
  const [goals, setGoals] = useState<Goal[]>([]);
  const [goalTitle, setGoalTitle] = useState("");
  const [goalEmoji, setGoalEmoji] = useState("ğŸ¯");
  const [goalPref, setGoalPref] = useState<number | null>(null); // attach new tasks to this goal
  const [priorityPref, setPriorityPref] = useState<Priority>("UI");

  // ===== Load from storage
  useEffect(() => {
    const raw = localStorage.getItem(TASKS_KEY);
    if (raw) {
      try {
        setTasks(JSON.parse(raw));
      } catch {}
    }
    const graw = localStorage.getItem(GOALS_KEY);
    if (graw) {
      try {
        setGoals(JSON.parse(graw));
      } catch {}
    }
    const gpref = localStorage.getItem(GOAL_PREF_KEY);
    if (gpref) {
      try {
        setGoalPref(JSON.parse(gpref));
      } catch {}
    }
    // wellness
    const w = localStorage.getItem(todayKey());
    if (w) {
      try {
        const obj = JSON.parse(w);
        if (typeof obj.mood === "number") setMood(obj.mood);
        if (typeof obj.stress === "number") setStress(obj.stress);
      } catch {}
    }
  }, []);

  // ===== Persist
  useEffect(() => {
    localStorage.setItem(TASKS_KEY, JSON.stringify(tasks));
  }, [tasks]);
  useEffect(() => {
    localStorage.setItem(GOALS_KEY, JSON.stringify(goals));
  }, [goals]);
  useEffect(() => {
    localStorage.setItem(GOAL_PREF_KEY, JSON.stringify(goalPref));
  }, [goalPref]);
  useEffect(() => {
    const k = todayKey();
    localStorage.setItem(
      k,
      JSON.stringify({ date: k.replace(WELLNESS_PREFIX, ""), mood, stress })
    );
  }, [mood, stress]);

  // ===== Phase 1: Gentle nudge after ~50 minutes of continuous activity
  const lastActiveRef = useRef<number>(Date.now());
  useEffect(() => {
    const onActive = () => (lastActiveRef.current = Date.now());
    window.addEventListener("mousemove", onActive);
    window.addEventListener("keydown", onActive);
    window.addEventListener("click", onActive);

    const iv = window.setInterval(() => {
      const since = Date.now() - lastActiveRef.current;
      const limit = 50 * 60_000; // 50 minutes
      if (since >= limit) {
        alert("Break time â¸ï¸ Youâ€™ve been focused ~50 minutes. Take 5 to recharge?");
        lastActiveRef.current = Date.now();
      }
    }, 30_000);

    return () => {
      window.clearInterval(iv);
      window.removeEventListener("mousemove", onActive);
      window.removeEventListener("keydown", onActive);
      window.removeEventListener("click", onActive);
    };
  }, []);

  // ===== Add task with goal + priority
  const addTask = () => {
    const text = input.trim();
    if (!text) return;
    const newTask: Task = {
      id: Date.now(),
      text,
      completed: false,
      createdAt: Date.now(),
      dueAt: dueAt || undefined,
      remind: remind || false,
      notified: false,
      pomos: 0,
      priority: priorityPref,
      goalId: goalPref ?? null,
    };
    setTasks((prev) => [newTask, ...prev]);
    setInput("");
    setDueAt("");
    setRemind(false);
  };

  // ===== Task actions
  const toggleTask = (id: number) => {
    setTasks((prev) =>
      prev.map((t) => (t.id === id ? { ...t, completed: !t.completed } : t))
    );
  };

  const removeTask = (id: number) => {
    setTasks((prev) => prev.filter((t) => t.id !== id));
    if (activeTaskId === id) setActiveTaskId(null);
  };

  const clearCompleted = () => {
    setTasks((prev) => prev.filter((t) => !t.completed));
  };

  const setTaskPriority = (id: number, p: Priority) => {
    setTasks((prev) => prev.map(t => t.id === id ? { ...t, priority: p } : t));
  };

  // ===== Derivations
  const remaining = useMemo(() => tasks.filter((t) => !t.completed).length, [tasks]);

  const filteredTasks = useMemo(() => {
    if (filter === "active") return tasks.filter((t) => !t.completed);
    if (filter === "completed") return tasks.filter((t) => t.completed);
    return tasks;
  }, [tasks, filter]);

  // ===== Notifications for due tasks
  const requestPermission = async () => {
    if (!("Notification" in window)) {
      alert("Notifications are not supported in this browser.");
      return;
    }
    const perm = await Notification.requestPermission();
    if (perm !== "granted") alert("Notification permission denied.");
  };

  const showReminder = (task: Task) => {
    if (!("Notification" in window)) return;
    if (Notification.permission !== "granted") return;
    new Notification("Task Reminder â°", { body: task.text, tag: String(task.id) });
  };

  useEffect(() => {
    const tick = () => {
      const now = Date.now();
      let changed = false;
      const next = tasks.map((t) => {
        if (!t.remind || t.notified || !t.dueAt) return t;
        const due = Date.parse(t.dueAt);
        if (!isNaN(due) && now >= due) {
          showReminder(t);
          changed = true;
          return { ...t, notified: true };
        }
        return t;
      });
      if (changed) setTasks(next);
    };
    const id = window.setInterval(tick, 30_000);
    tick();
    return () => window.clearInterval(id);
  }, [tasks]);

  // ===== Pomodoro link
  const handleFocusComplete = () => {
    if (activeTaskId == null) return;
    setTasks((prev) =>
      prev.map((t) =>
        t.id === activeTaskId ? { ...t, pomos: (t.pomos ?? 0) + 1 } : t
      )
    );
  };
  const activeTaskTitle = tasks.find((t) => t.id === activeTaskId)?.text;

  // ===== Wellness suggestion
  const wellnessSuggestion = useMemo(() => {
    if (stress >= 7) return "You're under a lot of stress. Break a big task into 3 tiny steps and take a 5-minute walk.";
    if (mood <= 2) return "Be kind to yourself. Pick one small, important task and celebrate finishing it.";
    if (mood >= 4 && stress <= 3) return "Great energy! Line up your top 3 important tasks and enter Focus mode.";
    return "Keep steady. Do one small step, then check in again.";
  }, [mood, stress]);

  // ===== Goals helpers
  const addGoal = () => {
    const title = goalTitle.trim();
    if (!title) return;
    const g: Goal = { id: Date.now(), title, emoji: goalEmoji };
    setGoals((prev) => [...prev, g]);
    setGoalTitle("");
  };

  const progressByGoal = useMemo(() => {
    const map = new Map<number, { total: number; done: number }>();
    for (const t of tasks) {
      if (t.goalId == null) continue;
      const v = map.get(t.goalId) ?? { total: 0, done: 0 };
      v.total += 1;
      if (t.completed) v.done += 1;
      map.set(t.goalId, v);
    }
    return map;
  }, [tasks]);

  // ===== Matrix view helper
  const labels: Record<Priority, string> = {
    UI: "Urgent + Important",
    UN: "Urgent + Not Important",
    NI: "Not Urgent + Important",
    NN: "Not Urgent + Not Important",
  };
  const quadrants: Priority[] = ["UI", "UN", "NI", "NN"];

  return (
    <>
      {/* Live animated background + brand header */}
      <LiveBackground />
      <header className="brand">
        <div className="brand__logo">
          AstraFocus <span>Nexus</span>
        </div>
        <p className="brand__tag">Define â€¢ Do â€¢ Done</p>
      </header>

      {/* Main app container */}
      <div className="container">
        <h1 className="title">
          Task Tracker <span className="badge">âœ…</span>
        </h1>

        {/* Wellness Check-in */}
        <div className="card">
          <h2 style={{ marginTop: 0 }}>Wellness check-in</h2>
          <div style={{ display: "flex", gap: 16, alignItems: "center", flexWrap: "wrap" }}>
            <div>
              <div style={{ fontSize: 12, opacity: 0.8, marginBottom: 6 }}>Mood</div>
              <div style={{ display: "flex", gap: 8 }}>
                {[1, 2, 3, 4, 5].map((v) => {
                  const emoji = ["ğŸ˜Ÿ", "ğŸ™", "ğŸ˜", "ğŸ™‚", "ğŸ˜„"][v - 1];
                  return (
                    <button
                      key={v}
                      className="emoji-btn"
                      aria-label={`Mood ${v}`}
                      onClick={() => setMood(v)}
                      style={{
                        border: mood === v ? "2px solid var(--accent)" : "1px solid rgba(255,255,255,.12)",
                      }}
                    >
                      {emoji}
                    </button>
                  );
                })}
              </div>
            </div>

            <div style={{ minWidth: 220 }}>
              <div style={{ fontSize: 12, opacity: 0.8, marginBottom: 6 }}>Stress: {stress}/10</div>
              <input type="range" min={0} max={10} value={stress} onChange={(e) => setStress(Number(e.target.value))} style={{ width: "100%" }} />
            </div>
          </div>

          <div className="tip" style={{ marginTop: 12 }}>
            <strong>Suggestion:</strong> {wellnessSuggestion}
          </div>

          {stress >= 7 && (
            <button
              className="btn"
              style={{ marginTop: 12 }}
              onClick={() => {
                const t = tasks.find((t) => !t.completed);
                if (t) {
                  alert(`Try this: Write 3 micro-steps for "${t.text}" and do the first in 2 minutes.`);
                } else {
                  alert("Add a task first, then break it into 3 micro-steps.");
                }
              }}
            >
              Break down my current task
            </button>
          )}
        </div>

        {/* Goals Panel */}
        <div className="card">
          <h2 style={{ marginTop: 0 }}>Personal goals</h2>
          <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
            <select value={goalEmoji} onChange={(e) => setGoalEmoji(e.target.value)}>
              {["ğŸ¯", "ğŸ‹ï¸", "ğŸ“š", "ğŸ’¼", "ğŸ’µ", "ğŸ§˜", "ğŸ§ ", "ğŸ’¡", "â¤ï¸", "ğŸƒ"].map((e) => (
                <option key={e} value={e}>
                  {e}
                </option>
              ))}
            </select>
            <input
              className="input"
              placeholder="Add a new goal (e.g., Stronger body)"
              value={goalTitle}
              onChange={(e) => setGoalTitle(e.target.value)}
            />
            <button className="btn" onClick={addGoal}>
              Add
            </button>
          </div>

          {goals.length === 0 ? (
            <div className="muted" style={{ marginTop: 8 }}>
              No goals yet. Add one, then link tasks to it.
            </div>
          ) : (
            <ul className="goals">
              {goals.map((g) => {
                const prog = progressByGoal.get(g.id) ?? { total: 0, done: 0 };
                const pct = prog.total ? Math.round((prog.done / prog.total) * 100) : 0;
                return (
                  <li key={g.id}>
                    <span className="goal-emoji">{g.emoji ?? "ğŸ¯"}</span>
                    <span className="goal-title">{g.title}</span>
                    <div className="bar">
                      <div className="fill" style={{ width: `${pct}%` }} />
                    </div>
                    <span className="goal-stat">
                      {prog.done}/{prog.total}
                    </span>
                    <button className="pill" onClick={() => setGoalPref(g.id)} title="Attach new tasks to this goal">
                      Use for new tasks
                    </button>
                  </li>
                );
              })}
            </ul>
          )}
        </div>

        {/* New Task Row */}
        <div className="row card" style={{ gap: 8, flexWrap: "wrap" }}>
          <input
            className="input"
            type="text"
            value={input}
            placeholder="Enter a task"
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && addTask()}
          />
          <input
            className="input"
            style={{ minWidth: 220 }}
            type="datetime-local"
            value={dueAt}
            onChange={(e) => setDueAt(e.target.value)}
            title="Due date & time"
          />
          <label style={{ display: "flex", alignItems: "center", gap: 6 }}>
            <input type="checkbox" checked={remind} onChange={(e) => setRemind(e.target.checked)} />
            Remind me
          </label>
          <label style={{ display: "flex", alignItems: "center", gap: 6 }}>
            Priority:
            <select value={priorityPref} onChange={(e) => setPriorityPref(e.target.value as Priority)}>
              <option value="UI">Urgent + Important</option>
              <option value="UN">Urgent + Not Important</option>
              <option value="NI">Not Urgent + Important</option>
              <option value="NN">Not Urgent + Not Important</option>
            </select>
          </label>
          <button className="btn" onClick={addTask}>
            Add Task
          </button>
          <button className="btn" onClick={requestPermission}>
            Enable Notifications
          </button>
        </div>

        {/* Pomodoro Panel */}
        <div className="card">
          <h2 style={{ marginTop: 0 }}>Pomodoro</h2>
          <PomodoroTimer activeTaskTitle={activeTaskTitle} onFocusComplete={handleFocusComplete} />
        </div>

        {/* Controls */}
        <div className="controls">
          <div className="filters">
            <button className={`chip ${filter === "all" ? "active" : ""}`} onClick={() => setFilter("all")}>
              All
            </button>
            <button className={`chip ${filter === "active" ? "active" : ""}`} onClick={() => setFilter("active")}>
              Active
            </button>
            <button className={`chip ${filter === "completed" ? "active" : ""}`} onClick={() => setFilter("completed")}>
              Completed
            </button>
            <button className={`chip ${filter === "matrix" ? "active" : ""}`} onClick={() => setFilter("matrix")}>
              Matrix
            </button>
          </div>

          <div className="meta">
            <span>{remaining} left</span>
            <button className="link" onClick={clearCompleted} disabled={tasks.every((t) => !t.completed)}>
              Clear completed
            </button>
          </div>
        </div>

        {/* Task List or Matrix */}
        {filter !== "matrix" ? (
          filteredTasks.length === 0 ? (
            <p className="empty">No tasks here yet.</p>
          ) : (
            <ul className="list">
              {filteredTasks.map((task) => (
                <li className="item" key={task.id}>
                  <input
                    type="checkbox"
                    checked={task.completed}
                    onChange={() => toggleTask(task.id)}
                    title="Toggle complete"
                  />
                  <span className={`text ${task.completed ? "done" : ""}`}>
                    {task.text}
                    {task.dueAt && (
                      <small style={{ marginLeft: 8, color: "#475569" }}>
                        (due {new Date(task.dueAt).toLocaleString()})
                      </small>
                    )}
                    {task.remind && !task.notified && <small> â€¢ will remind</small>}
                    {task.remind && task.notified && <small> â€¢ reminded</small>}
                    {typeof task.pomos === "number" && task.pomos > 0 && (
                      <small style={{ marginLeft: 8, color: "#475569" }}>â€¢ {task.pomos} pomos</small>
                    )}
                  </span>

                  <select
                    value={task.priority ?? "NN"}
                    onChange={(e) => setTaskPriority(task.id, e.target.value as Priority)}
                    title="Eisenhower priority"
                    style={{ marginRight: 8 }}
                  >
                    <option value="UI">UI</option>
                    <option value="UN">UN</option>
                    <option value="NI">NI</option>
                    <option value="NN">NN</option>
                  </select>

                  <button
                    className={`pill ${activeTaskId === task.id ? "active" : ""}`}
                    onClick={() => setActiveTaskId(activeTaskId === task.id ? null : task.id)}
                    title="Set as active for Pomodoro"
                  >
                    ğŸ¯ Focus
                  </button>
                  <button className="icon" onClick={() => removeTask(task.id)} aria-label="Delete">
                    âœ•
                  </button>
                </li>
              ))}
            </ul>
          )
        ) : (
          <div className="matrix">
            {quadrants.map((q) => {
              const items = tasks.filter((t) => (t.priority ?? "NN") === q);
              return (
                <div key={q} className="matrix-cell">
                  <div className="matrix-title">{labels[q]}</div>
                  <ul className="list" style={{ marginTop: 6 }}>
                    {items.length === 0 ? (
                      <li className="item">
                        <span className="text muted">No tasks.</span>
                      </li>
                    ) : (
                      items.map((t) => (
                        <li className="item" key={t.id}>
                          <input
                            type="checkbox"
                            checked={t.completed}
                            onChange={() => toggleTask(t.id)}
                            title="Toggle complete"
                          />
                          <span className={`text ${t.completed ? "done" : ""}`}>{t.text}</span>
                          {!t.completed && (
                            <div style={{ display: "flex", gap: 6, alignItems: "center", marginLeft: "auto" }}>
                              {quadrants.map((opt) => (
                                <button
                                  key={opt}
                                  className={`pill ${((t.priority ?? "NN") === opt && "active") || ""}`}
                                  onClick={() => setTaskPriority(t.id, opt)}
                                  title={labels[opt]}
                                >
                                  {opt}
                                </button>
                              ))}
                            </div>
                          )}
                        </li>
                      ))
                    )}
                  </ul>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </>
  );
};

export default App;
