import React, { useMemo } from "react";
interface Props { mood:number; setMood:(v:number)=>void; stress:number; setStress:(v:number)=>void; suggestBreakdown:()=>void; }
const Wellness:React.FC<Props>=({mood,setMood,stress,setStress,suggestBreakdown})=>{
  const suggestion=useMemo(()=>{
    if(stress>=7) return "You're under a lot of stress. Break a big task into 3 tiny steps and take a 5-minute walk.";
    if(mood<=2) return "Be kind to yourself. Pick one small, important task and celebrate finishing it.";
    if(mood>=4 && stress<=3) return "Great energy! Line up your top 3 important tasks and enter Focus mode.";
    return "Keep steady. Do one small step, then check in again.";
  },[mood,stress]);
  return (
    <div className="card">
      <h2 style={{marginTop:0}}>Wellness check-in</h2>
      <div style={{display:"flex",gap:16,alignItems:"center",flexWrap:"wrap"}}>
        <div>
          <div style={{fontSize:12,opacity:.8,marginBottom:6}}>Mood</div>
          <div style={{display:"flex",gap:8}}>
            {[1,2,3,4,5].map(v=>{
              const emoji=["??","??","??","??","??"][v-1];
              return <button key={v} className="emoji-btn" onClick={()=>setMood(v)} aria-label={`Mood ${v}`} style={{border:mood===v?"2px solid var(--accent)":"1px solid rgba(255,255,255,.12)"}}>{emoji}</button>
            })}
          </div>
        </div>
        <div style={{minWidth:220}}>
          <div style={{fontSize:12,opacity:.8,marginBottom:6}}>Stress: {stress}/10</div>
          <input type="range" min={0} max={10} value={stress} onChange={e=>setStress(Number(e.target.value))} style={{width:"100%"}}/>
        </div>
      </div>
      <div className="tip" style={{marginTop:12}}><strong>Suggestion:</strong> {suggestion}</div>
      {stress>=7 && <button className="btn" style={{marginTop:12}} onClick={suggestBreakdown}>Break down my current task</button>}
    </div>
  );
};
export default Wellness;
